import Info from "./Info";
import Nav from "./Nav";

function App() {
  const friends = [
    { name: "Mars", age: 16 },
    { name: "Jane", age: 18 },
    { name: "Nick", age: 20 },
  ];

  function buttonHandler() {
    alert("你按了一次");
  }

  return (
    <div>
      <Nav />
      {friends.map((friend) => (
        <Info name={friend.name} age={friend.age} />
      ))}
      ;<bitton onClick={buttonHandler}></bitton>
    </div>
  );
}

export default App;
