/* eslint-disable no-template-curly-in-string */
import React, { useState, useEffect } from "react";
import Search from "../components/Search";
import Picture from "../components/Picture";
import axios from "axios";

const Homepage = () => {
  const [input, setInput] = useState("");
  const [data, setData] = useState(null);

  const auth = "2vl8J9MHfKF8VZXBWnJaTw3LSndKwNmebUPeHDRoiC73TAbRnhPOqONt";
  const initialURL = "https://api.pexels.com/v1/curated";
  const searchURL =
    "https://api.pexels.com/v1/search?query=${input}&page=1&per_page=15";

  const search = async (url) => {
    let result = await axios.get(url, {
      headers: { Authorization: auth },
    });
    // console.log(result);
    setData(result.data.photos);
  };

  useEffect(() => {
    search(initialURL);
  }, []);

  return (
    <div style={{ minHeight: "100vh" }}>
      <Search
        search={() => {
          search(searchURL);
        }}
        setInput={setInput}
      />
      <div className="pictures">
        {data &&
          data.map((d, index) => {
            return <Picture data={d} key={index} />;
          })}
      </div>
    </div>
  );
};

export default Homepage;
