import React, { useState } from "react";
import Info from "./Info";
import Create from "./Create";

function App() {
  const [myName, setMyName] = useState("Mike Huang");

  const buttonHandler = () => {
    setMyName("王大明");
  };
  return (
    <div>
      <h1>{myName}</h1>
      <button onClick={buttonHandler}>改變姓名</button>
    </div>
  );
}

export default App;
